import React, {useState} from 'react';

import "./ExpenseForm.css";



function ExpenseForm(props) {
    const [enteredTitle, setEnteredTitle] = useState(props.title);
    const [amountChange, setAmountChange] = useState(props.amount);
    const [dateChange, setDateChanged] = useState(props.date);

    const titleChangeHandler = (event)=>{
        setEnteredTitle(event.target.value);
    }
    const amountChangeHandler = (event)=>{
        setAmountChangedHandler(event.target.value);
    }
    const dateChangeHandler = (event)=>{
        setDateChangedHandler(event.target.value);
    }

  return (
   <form>
    <div className='new-expense__controls'>
        <div className='new-expense__control'>
            <label>Title</label>
            <input type='text' onChange={titleChangeHandler} value={enteredTitle}/>
        </div>
        <div className='new-expense__control' >
            <label>Amount</label>
            <input type='number' min="0.01" step="0.01" onChange={amountChangeHandler}/>
        </div>
        <div className='new-expense__control' onChange={amountChangeHandler}>
            <label>Date</label>
            <input type='date' min="2019-01-01" max="2028-12-31" />
        </div>
        <div className="new-expense__actions">
            <button type='submit'>
                Add Expense
            </button>

        </div>
    </div>
   </form>
  );
}

export default ExpenseForm;
